}

.page-title div {
  clip-path: polygon(0 0, var(--clip) 0, var(--clip) 100%, 0% 100%);
}


.links {
  grid-area: links;
  padding-top: 1rem;
}

.demos div:not(:first-child),
.links div:not(:first-child) {
  margin-left: 1rem;
}

.frame__demo {
	color: #313131;
  display: inline-block;
}

